/*
 * Editable jqury tabs
 * 
 * DO NOT EDIT THIS FILE. EDIT TAB_STYLE.css
 * 
 */

/*
 * return the main tab object
 */
function getTabPane(menuItem){
	return $(menuItem).closest(".tab-pane");
}

$(document).ready(function(){
	
	//init
	//always aktive first
	panes = $(".tab-pane");
	for (var j = 0; j < $(panes).size(); j++){
		pane = $(panes).get(j);
		i = $(pane).find(".tab-menu-item").index(this);
		$(pane).children(".tab-body:eq(0)").addClass("tab-body-active");
		$(pane).find(".tab-menu-item:eq(0)").addClass("tab-menu-item-active");
	}
	
	//over the tab
	$(".tab-menu-item").bind("mouseover", function(){
		$(this).addClass("tab-menu-item-over");
	});
	//out the tab
	$(".tab-menu-item").bind("mouseout", function(){
		$(this).removeClass("tab-menu-item-over");
	});
	//click on tab
	$(".tab-menu-item").bind("click", function(){
		pane = getTabPane(this);
		i = $(pane).find(".tab-menu-item").index(this);
		//hide active tab and body
		$(pane).children(".tab-body-active").removeClass("tab-body-active");
		$(pane).find(".tab-menu-item-active").removeClass("tab-menu-item-active");
		//show new active tab and body
		$(pane).children(".tab-body:eq(" + i + ")").addClass("tab-body-active");
		$(pane).find(".tab-menu-item:eq(" + i + ")").addClass("tab-menu-item-active");
	});
});